mapper.select:
SELECT a.iboard, a.public_fl publicFl, a.icategory, c.name categoryNm, d.name subCategoryNm, b.nm, a.title, a.created_at createdAt FROM board a INNER JOIN user b ON a.iuser = b.iuser INNER JOIN board_category c ON a.icategory = c.icategory INNER JOIN board_subcategory d ON a.isubcategory = d.isubcategory
a.icategory = #{icategory}
ORDER BY a.iboard DESC LIMIT #{pageStart}, #{amount}

update.foreach:
UPDATE board SET
public_fl = #{publicFl}
WHERE iboard = #{iboard}

mapper.delete:
DELETE FROM board_pic WHERE iboard IN(

    #{iboard}

)

DELETE FROM board WHERE iboard IN(

    #{iboard}

)
