mapper.select:
SELECT a.iboard, a.icategory, b.nm, a.title, a.created_at createdAt FROM board a INNER JOIN user b ON a.iuser = b.iuser
a.public_fl = 'Y' AND title != '' AND contents != ''
AND a.icategory = #{icategory}
AND title LIKE ('%${keyword}%')
ORDER BY a.iboard DESC LIMIT #{pageStart}, #{amount}

SELECT uuid_name
FROM board_pic
WHERE iboard = #{iboard}

SELECT iboard, iboardfile, original_name originalName, uuid_name uuidName
FROM board_file
WHERE iboard = #{iboard}

SELECT iboard, original_name originalName, uuid_name uuidName
FROM board_file
WHERE iboardfile = #{iboardfile}

mapper.update:
UPDATE board SET
title = #{title},
contents = #{contents}
WHERE iboard = #{iboard}

mapper.select:
SELECT COUNT(*) FROM board
icategory = #{icategory}
title LIKE '%${keyword}%'
