mapper.select:
SELECT a.iboard, a.icategory, a.isubject, b.nm, a.title, a.created_at createdAt FROM board a INNER JOIN user b ON a.iuser = b.iuser
a.public_fl = 'Y'
AND a.isubject = #{subject}
AND title LIKE ('%${keyword}%')
ORDER BY a.iboard DESC LIMIT #{pageStart}, #{amount}

mapper.update:
UPDATE board SET
title = #{title},
contents = #{contents}
WHERE iboard = #{iboard}

mapper.select:
SELECT COUNT(*) FROM board
isubject = #{isubject}
title LIKE '%${keyword}%'
